import { Component, ElementRef, ViewChild } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent 
{

  @ViewChild('canvas', { static: true, read: ElementRef}) canvas!: ElementRef;

  private ctx!: CanvasRenderingContext2D;

  // Define the points as {x, y}
  start = { x: 50, y: 20 };
  cp1 = { x: 230, y: 30 };
  cp2 = { x: 150, y: 80 };
  end = { x: 250, y: 100 };

  ngOnInit(): void 
  {
    this.ctx = this.canvas.nativeElement.getContext('2d');

    // Cubic Bézier curve
    this.ctx.beginPath();
    this.ctx.moveTo(this.start.x, this.start.y);
    this.ctx.bezierCurveTo(this.cp1.x, this.cp1.y, this.cp2.x, this.cp2.y, this.end.x, this.end.y);
    this.ctx.stroke();

    // Start and end points
    this.ctx.fillStyle = "blue";
    this.ctx.beginPath();
    this.ctx.arc(this.start.x, this.start.y, 5, 0, 2 * Math.PI); // Start point
    this.ctx.arc(this.end.x, this.end.y, 5, 0, 2 * Math.PI); // End point
    this.ctx.fill();

    // Control points
    this.ctx.fillStyle = "red";
    this.ctx.beginPath();
    this.ctx.arc(this.cp1.x,this.cp1.y, 5, 0, 2 * Math.PI); // Control point one
    this.ctx.arc(this.cp2.x, this.cp2.y, 5, 0, 2 * Math.PI); // Control point two
    this.ctx.fill();
  }
}
