import { Component, ElementRef, ViewChild } from '@angular/core';

interface vector
{
  x : number,
  y : number
}

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent 
{

  @ViewChild('canvas', { static: true, read: ElementRef}) canvas!: ElementRef;

  private ctx!: CanvasRenderingContext2D;

  // Define the points as {x, y}
  start = { x: 50, y: 20 };
  cp1 = { x: 230, y: 30 };
  cp2 = { x: 150, y: 80 };
  end = { x: 250, y: 100 };

  move : boolean = false;
  mousePosition = {mousePositionX : 0, mousePositionY : 0}

  ngOnInit(): void 
  {
    this.ctx = this.canvas.nativeElement.getContext('2d');
  }

  dragPoint(evt : any) 
  {
    var rect = this.ctx.canvas.getBoundingClientRect();
    this.mousePosition.mousePositionX = evt.clientX - rect.left;
    this.mousePosition.mousePositionY = evt.clientY - rect.top;
    let controlOne : vector = { x : this.mousePosition.mousePositionX, y : this.mousePosition.mousePositionY};

   // this.drawPoint(this.mousePosition.mousePositionX, this.mousePosition.mousePositionY);
   this.ctx.clearRect(0, 0, this.ctx.canvas.width, this.ctx.canvas.height);
   this.drawBezierCurve(this.start, controlOne, this.cp2, this.end, 'black');
   this.drawControlPoint(controlOne, 'red')
   this.drawControlPoint(this.cp2, 'red')

  }

  drawBezierCurve(startPosition : vector, firstControlPoint : vector, secondControlPoint : vector, endPosition : vector, color : string = 'red')
  {
    if(!this.move) return;

    // Cubic Bézier curve
    this.ctx.fillStyle = color;
    this.ctx.beginPath();
    this.ctx.moveTo(startPosition.x, startPosition.y);
    this.ctx.bezierCurveTo(firstControlPoint.x, firstControlPoint.y, secondControlPoint.x, secondControlPoint.y, endPosition.x, endPosition.y);
    this.ctx.stroke();
  }

  drawControlPoint(controlPosition : vector, color : string = 'blue')
  {
    if(!this.move) return;
     // Control points
     this.ctx.fillStyle = color;
     this.ctx.beginPath();
     this.ctx.arc(controlPosition.x,controlPosition.y, 5, 0, 2 * Math.PI);
     this.ctx.fill(); 
  }

  mouseUp()
  {
    this.move = false;
  }

  mouseDown()
  {
    this.move = true;
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta http-equiv="X-UA-Compatible" content="IE=edge">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Angular Tests</title>
</head>
<body>
    <canvas (mousemove)="dragPoint($event)" (mousedown)="mouseDown()" (mouseup)="mouseUp()" #canvas width="600" height="300"></canvas>
</body>
</html>
