<head>
  <meta charset="UTF-8">
  <meta http-equiv="X-UA-Compatible" content="IE=edge">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Angular Tests</title>
</head>
<body>
    <canvas (mousemove)="mousePosition($event)" (mousedown)="mouseDown()" (mouseup)="mouseUp()" #canvas width="600" height="300"></canvas>
</body>
