import { Component, ElementRef, ViewChild } from '@angular/core';

interface vector
{
  x : number,
  y : number
}

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent 
{

  @ViewChild('canvas', { static: true, read: ElementRef}) canvas!: ElementRef;

  private ctx!: CanvasRenderingContext2D;

  // Define the points as {x, y}
  startPoint = { x: 50, y: 20 };
  controlPointOne = { x: 230, y: 30 };
  controlPointTwo = { x: 150, y: 80 };
  endPoint = { x: 250, y: 100 };

  move : boolean = false;
  mousePos = {x : 0, y : 0}
  controlPointRadius : number = 5;

  async ngOnInit(): Promise<void> 
  {
    await this.ctx = this.canvas.nativeElement.getContext('2d');
    this.animatioLoop();
  }

  animatioLoop()
  {
    if(!this.ctx) return;

    this.ctx.clearRect(0, 0, this.ctx.canvas.width, this.ctx.canvas.height);
    this.drawBezierCurve(this.startPoint, this.controlPointOne, this.controlPointTwo, this.endPoint, 'black');
    this.drawControlPoint(this.startPoint, this.controlPointRadius, 'blue');
    this.drawControlPoint(this.endPoint, this.controlPointRadius, 'blue');
    this.drawControlPoint(this.controlPointOne, this.controlPointRadius, 'red');
    this.drawControlPoint(this.controlPointTwo, this.controlPointRadius, 'red');
    requestAnimationFrame(this.animatioLoop);
  }

  mousePosition(evt : any) : vector
  {
    var rect = this.ctx.canvas.getBoundingClientRect();
    this.mousePos.x = evt.clientX - rect.left;
    this.mousePos.y = evt.clientY - rect.top;
    let mousePosition : vector = { x : this.mousePos.x, y : this.mousePos.y};

    return mousePosition;
  }

  drawBezierCurve(startPosition : vector, firstControlPoint : vector, secondControlPoint : vector, endPosition : vector, color : string = 'red')
  {
    this.ctx.fillStyle = color;
    this.ctx.beginPath();
    this.ctx.moveTo(startPosition.x, startPosition.y);
    this.ctx.bezierCurveTo(firstControlPoint.x, firstControlPoint.y, secondControlPoint.x, secondControlPoint.y, endPosition.x, endPosition.y);
    this.ctx.stroke();
  }

  drawControlPoint(controlPosition : vector, radius : number = 5, color : string = 'blue')
  {
    this.ctx.fillStyle = color;
    this.ctx.beginPath();
    this.ctx.arc(controlPosition.x,controlPosition.y, radius, 0, 2 * Math.PI);
    this.ctx.fill(); 
  }

  verifyMouseOverControlpoint(event : any, controlPosition : vector)
  {
    if (this.mousePosition(event).x > controlPosition.x - this.controlPointRadius &&
        this.mousePosition(event).x < controlPosition.x + this.controlPointRadius &&
        this.mousePosition(event).y > controlPosition.y - this.controlPointRadius &&
        this.mousePosition(event).y < controlPosition.y + this.controlPointRadius)
        {
          console.log(this.mousePosition(event));
          controlPosition = this.mousePosition(event);
        }
  }

  mouseUp()
  {
    this.move = false;
  }

  mouseDown()
  {
    this.move = true;
  }
}
